public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int h = sc.nextInt();
        int w = sc.nextInt();
        int[][] map = new int[h+1000][w+1000];
        for(int i=0; i<h; i++){
            String s = sc.next();
            for(int j=0; j<w; j++){
                if(s.charAt(j) == '#'){
                    map[i+350][j+350] = 1;
                }
            }
        }
        sc.close();
        int[][] sum = new int[h+1000][w+1000];
        for(int i=-5; i<h+w+5; i++){
            int mi = 350 - w + i;
            int mj = 350;
            for(int j=-200; j<w+200; j++){
                int mi1 = mi + j;
                int mj1 = mj + j;
                sum[mi1][mj1] = sum[mi1-1][mj1-1];
                if(map[mi1][mj1] == 1){
                    sum[mi1][mj1]++;
                }
            }
        }
        long ans = 0;
        long ans2 = 0;
        for(int i=-5; i<h+w+5; i++){
            int mi = 350 - w + i;
            int mj = 350;
            for(int j=-5; j<w+5; j++){
                int mi1 = mi+j;
                int mj1 = mj+j;
                if(map[mi1][mj1] == 1){
                    for(int k=j+1; k<w+5; k++){
                        int mi2 = mi + k;
                        int mj2 = mj + k;
                        if(map[mi2][mj2] == 1){
                            int d = k-j;
                            ans += sum[mi2+d-1][mj2-d-1] - sum[mi1+d][mj1-d];
                            ans += sum[mi2-d-1][mj2+d-1] - sum[mi1-d][mj1+d];
                            ans2 += (map[mi2+d][mj2-d]) + (map[mi2-d][mj2+d]) + (map[mi1+d][mj1-d]) + (map[mi1-d][mj1+d]);
                        }
                    }
                }
            }
        }
        for(int i=0; i<sum.length; i++){
            Arrays.fill(sum[i], 0);
        }
        for(int i=-5; i<h+w+5; i++){
            int mi = 350 - w + i;
            int mj = 350 + w;
            for(int j=-200; j<w+200; j++){
                int mi1 = mi + j;
                int mj1 = mj - j;
                sum[mi1][mj1] = sum[mi1-1][mj1+1];
                if(map[mi1][mj1] == 1){
                    sum[mi1][mj1]++;
                }
            }
        }
        for(int i=-5; i<h+w+5; i++){
            int mi = 350 - w + i;
            int mj = 350 + w;
            for(int j=-5; j<w+5; j++){
                int mi1 = mi+j;
                int mj1 = mj-j;
                if(map[mi1][mj1] == 1){
                    for(int k=j+1; k<w+5; k++){
                        int mi2 = mi + k;
                        int mj2 = mj - k;
                        if(map[mi2][mj2] == 1){
                            int d = k-j;
                            ans += sum[mi2+d-1][mj2+d+1] - sum[mi1+d][mj1+d];
                            ans += sum[mi2-d-1][mj2-d+1] - sum[mi1-d][mj1-d];
                            ans2 += (map[mi2+d][mj2+d]) + (map[mi2-d][mj2-d]) + (map[mi1+d][mj1+d]) + (map[mi1-d][mj1-d]);
                        }
                    }
                }
            }
        }
        System.out.println(ans + ans2/2);
    }
}

public class Main {
	public static void main(String[] args) {
		new Main().solver();
	}
	int UNUSED = 1 << 60;
	@SuppressWarnings("unchecked")
	void solver() {
		Scanner sc = new Scanner(System.in);
		while (true) {
			H = sc.nextInt();
			W = sc.nextInt();
			if (H == 0 && W == 0)
				break;
			char[][] table = new char[H][W];
			memo = new int[H][W][H][W];
			for (int i = 0; i < H; i++) {
				for (int j = 0; j < W; j++) {
					for (int k = 0; k < H; k++) {
						for (int l = 0; l < W; l++) {
							memo[i][j][k][l] = UNUSED;
						}
					}
				}
			}
			p = new ArrayList[58];
			for (int i = 0; i < 58; i++) {
				p[i] = new ArrayList<>();
			}
			for (int i = 0; i < H; i++) {
				table[i] = sc.next().toCharArray();
				for (int j = 0; j < W; j++) {
					if (table[i][j] == '.' || table[i][j] == '#')
						continue;
					p[table[i][j] - 'A'].add(new Coordinate(j, i));
				}
			}
			int d = rec(0, 0, table, W - 1, H - 1);
			System.out.println(d < 0 ? -1 : d);
		}
	}
	ArrayList<Coordinate>[] p;
	class Coordinate {
		int x;
		int y;
		public Coordinate(int x, int y) {
			this.x = x;
			this.y = y;
		}
	}
	int[][][][] memo;
	int H, W;
	int rec(int curX, int curY, char[][] table, int toX, int toY) {
		if (curY < 0 || curX < 0 || curY >= H || curX >= W || toY < curY || toX < curX || table[curY][curX] == '#')
			return -(1 << 30);
		if (curY == toY && curX == toX) {
			return 0;
		}
		if (memo[curY][curX][toY][toX] != UNUSED) {
			return memo[curY][curX][toY][toX];
		}
		int ret = -(1 << 30);
		for (int i = 0; i < 2; i++) {
			ret = Math.max(ret, rec(curX + i, curY + (i ^ 1), table, toX, toY));
		}
		if (table[curY][curX] == '.')
			return ret;
		if (isLowerCase(table[curY][curX])) {
			for (Coordinate P : p[String.valueOf(table[curY][curX]).toUpperCase().charAt(0) - 'A']) {
				if (P.x < curX || P.y < curY || toX < P.x || toY < P.y)
					continue;
				if (P.x == curX || P.y == curY)
					throw new AssertionError();
				int tmp = 0;
				if ((P.x == curX && P.y == curY + 1) || (P.x == curX + 1 || P.y == curY + 1)) {
					tmp = 1;
				} else {
					for (int i = 0; i < 2; i++) {
						for (int j = 0; j < 2; j++) {
							tmp = Math.max(tmp, rec(curX + i, curY + (i ^ 1), table, P.x - j, P.y - (j ^ 1)) + 1);
						}
					}
				}
				if (toX == P.x && toY == P.y) {
					ret = Math.max(ret, tmp);
				} else {
					for (int i = 0; i < 2; i++) {
						ret = Math.max(ret, tmp + rec(P.x + i, P.y + (i ^ 1), table, toX, toY));
					}
				}
			}
		}
		memo[curY][curX][toY][toX] = ret;
		return ret;
	}
	boolean isLowerCase(char c) {
		if (String.valueOf(c).toLowerCase().charAt(0) - c == 0) {
			return true;
		} else {
			return false;
		}
	}
}

public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskC solver = new TaskC();
        solver.solve(1, in, out);
        out.close();
    }
    static class TaskC {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int n = in.nextInt();
            int k = in.nextInt();
            TreeMap<Long, Long> map = new TreeMap<>();
            for (int i = 0; i < n; i++) {
                long a = in.nextInt();
                long b = in.nextInt();
                if (map.containsKey(a)) {
                    map.put(a, map.get(a) + b);
                } else {
                    map.put(a, b);
                }
            }
            Set<Long> set = map.keySet();
            int sum = 0;
            for (long x : set) {
                sum += map.get(x);
                if (sum >= k) {
                    out.println(x);
                    return;
                }
            }
        }
    }
    static class InputReader {
        BufferedReader in;
        StringTokenizer tok;
        public String nextString() {
            while (!tok.hasMoreTokens()) {
                try {
                    tok = new StringTokenizer(in.readLine(), " ");
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
            }
            return tok.nextToken();
        }
        public int nextInt() {
            return Integer.parseInt(nextString());
        }
        public InputReader(InputStream inputStream) {
            in = new BufferedReader(new InputStreamReader(inputStream));
            tok = new StringTokenizer("");
        }
    }
}

