public static void CountingSort(int A[],int B[],int k){
    int i,j;
    for(i=0;i<k+1;i++){
	C[i] = 0;
    }
    for(j=0;j<n;j++){
	C[A[j]]++;
    }
    for(i=1;i<k+1;i++){
	C[i] = C[i] + C[i-1];
    }
    for(j=n-1;j>=0;j--){
	B[C[A[j]]] = A[j];
	C[A[j]]--;
    }
}
}

