  public static void main(String[] args) {
    InputStream inputStream;
    if (args.length > 0 && args[0].equals("devTesting")) {
      try {
        inputStream = new FileInputStream(args[1]);
      } catch(FileNotFoundException e) {
        throw new RuntimeException(e);
      }
    } else {
      inputStream = System.in;
    }
    OutputStream outputStream = System.out;
    InputReader in = new InputReader(inputStream);
    PrintWriter out = new PrintWriter(outputStream);
    TaskC solver = new TaskC();
    solver.solve(1, in, out);
    out.close();
  }
  static class TaskC {    
    int n, a, x;
    int[] cards;
    long[][][] state;
    void solve(int testNumber, InputReader in, PrintWriter out) {
      n = in.nextInt();
      a = in.nextInt();
      cards = new int[n];
      for (int i = 0; i < n; ++i) {
        cards[i] = in.nextInt();
      }
      state = new long[n][n * n + 1][n + 1];
      for (long[][] i : state) {
        for (long[] j : i) {
          Arrays.fill(j, -1L);
        }
      }
      out.println(go(n - 1, 0, 0));
    }
    long go(int pos, int s, int t) {
      if (pos < 0) {
        if (t > 0 && s % t == 0 && s / t == a) {
          return 1L;
        }
        return 0L;
      }
      if (state[pos][s][t] != -1L) {
        return state[pos][s][t];
      }
      long ans = go(pos - 1, s + cards[pos], t + 1) + go(pos - 1, s, t);
      state[pos][s][t] = ans;
      return ans;
    }
  }
  static class InputReader {
    public BufferedReader reader;
    public StringTokenizer tokenizer;
    public InputReader(InputStream stream) {
      reader = new BufferedReader(new InputStreamReader(stream));
      tokenizer = null;
    }
    public String next() {
      while (tokenizer == null || !tokenizer.hasMoreTokens()) {
        try {
          tokenizer = new StringTokenizer(reader.readLine());
        } catch (IOException e) {
          throw new RuntimeException(e);
        }
      }
      return tokenizer.nextToken();
    }
    public String nextLine() {
      try {
        return reader.readLine();
      } catch(IOException e) {
        throw new RuntimeException(e);
      }
    }
    public int nextInt() {
      return Integer.parseInt(next());
    }
    public long nextLong() {
      return Long.parseLong(next());
    }
    public double nextDouble() {
      return Double.parseDouble(next());
    }
    public boolean hasInput() {
      try {
        if (tokenizer != null && tokenizer.hasMoreTokens()) {
          return true;
        }
        reader.mark(1);
        int ch = reader.read();
        if (ch != -1) {
          reader.reset();
          return true;
        }
        return false;
      } catch(IOException e) {
        throw new RuntimeException(e);
      }
    }
  }
}

public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        MyScanner in = new MyScanner(inputStream);
        MyPrintWriter out = new MyPrintWriter(outputStream);
        TaskD solver = new TaskD();
        solver.solve(1, in, out);
        out.close();
    }
    static class TaskD {
        public void solve(int testNumber, MyScanner in, MyPrintWriter out) {
            long A = in.nextLong(), B = in.nextLong();
            long res = solve(A, B);
            out.println(res);
        }
        private long solve(long A, long B) {
            if (A == B) return 1;
            if (A == 0) {
                throw new RuntimeException();
            }
            if (A > B) throw new RuntimeException();
            long a = Long.highestOneBit(A);
            long b = Long.highestOneBit(B);
            if (a == b) return solve(A ^ a, B ^ b);
            long res = b - A;
            long b2 = Long.highestOneBit(B ^ b) << 1;
            A = Math.max(A, b2);
            return res + b - A + b2;
        }
    }
    static class MyPrintWriter {
        PrintWriter out;
        public MyPrintWriter(OutputStream outputStream) {
            out = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }
        public MyPrintWriter(Writer writer) {
            out = new PrintWriter(writer);
        }
        public void println(Object... os) {
            if (os.length == 0) {
                out.println();
                return;
            }
            for (int i = 0; i < os.length - 1; i++) {
                out.print(os[i]);
                out.print(' ');
            }
            out.println(os[os.length - 1]);
        }
        public void close() {
            out.close();
        }
    }
    static class MyScanner {
        private final InputStream in;
        private static final int BUFSIZE = 65536;
        int bufLen;
        int bufPtr;
        byte[] buf = new byte[BUFSIZE];
        public MyScanner(InputStream in) {
            this.in = in;
        }
        public int read() {
            if (bufLen == -1)
                throw new InputMismatchException();
            if (bufPtr >= bufLen) {
                bufPtr = 0;
                try {
                    bufLen = in.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (bufLen <= 0)
                    return -1;
            }
            return buf[bufPtr++];
        }
        public long nextLong() {
            int c = read();
            if (c == -1) throw new NoSuchElementException();
            while (c != '-' && (c < '0' || '9' < c)) {
                c = read();
                if (c == -1) throw new NoSuchElementException();
            }
            if (c == '-') return -nextLong();
            long res = 0;
            do {
                res *= 10;
                res += c - '0';
                c = read();
            } while ('0' <= c && c <= '9');
            return res;
        }
    }
}

