public class Main {
	static int mod = (int) 1e9 + 7;
	public static void main(String[] args) {
		FastScanner fs = new FastScanner(System.in);
		int n = fs.nextInt();
		int a[] = fs.nextIntArray(n);
		long p[][] = new long[n][n];
		long sum[] = new long[n];
		for(int i=1;i<n;i++) sum[i] = plus(sum[i-1], div(1,i+1));
		long ans = 0;
		for(int i=0;i<n;i++) {
			int l = i;
			int r = n-1-i;
			long now = plus(sum[l], sum[r]);
			now = plus(now,1); 
			ans = plus(ans, mul(now, a[i]));
		}
		for(int i=1;i<=n;i++) {
			ans = mul(ans, i);
		}
		System.out.println(ans);
	}
	static long plus(long x, long y) {
		x %= mod;
		y %= mod;
		long res = (x + y) % mod;
		return res;
	}
	static long suv(long x, long y) {
		x %= mod;
		y %= mod;
		long res = (x - y + mod) % mod;
		return res;
	}
	static long mul(long x, long y) {
		x %= mod;
		y %= mod;
		long res = x * y % mod;
		return res;
	}
	static long div(long x, long y) {
		x %= mod;
		y %= mod;
		long res = x * modpow(y, mod - 2) % mod;
		return res;
	}
	static long modpow(long a, long p) {
		if (p == 0)
			return 1;
		if (p % 2 == 0) {
			long halfP = p / 2;
			long root = modpow(a, halfP);
			return root * root % mod;
		} else
			return a * modpow(a, p - 1) % mod;
	}
}
class FastScanner {
