public class Main{
    static MyReader in = new MyReader();
    static PrintWriter out = new PrintWriter(System.out);
    public static void main(String[] args){
        long n = in.l();
        long s = in.l();
        if(s == n){
            out.println(n+1);
        } else {
            final double rootN = Math.sqrt(n-1)+1;
            boolean flag = true;
            for(int b = 2; b < rootN; b++){
                long sum = 0;
                for(long i = n; i > 0; i /= b){
                    sum += i % b;
                }
                if(sum == s){
                    out.println(b);
                    flag = false;
                    break;
                }
            }
            if(flag){
                for(int p = 1; p < rootN; p++){
                    if((n-s)%p == 0){
                        long b = (n-s)/p+1;
                        long sum = 0;
                        for(long i = n; i > 0; i /= b){
                            sum += i % b;
                        }
                        if(sum == s){
                            out.println(b);
                            flag = false;
                            break;
                        }
                    }
                }
                if(flag){
                    out.println(-1);
                }
            }
        }
        out.flush();
    }
