			long a2 = Math.min(min2, y);
			long a3 = Math.max(max1, x);
			long a4 = Math.max(max2, y);
			long b1 = Math.min(min1, y);
			long b2 = Math.min(min2, x);
			long b3 = Math.max(max1, y);
			long b4 = Math.max(max2, x);
			if((a3-a1)*(a4-a2)<=(b3-b1)*(b4-b2)){
				min1 = a1;
				min2 = a2;
				max1 = a3;
				max2 = a4;
			}else{
				min1 = b1;
				min2 = b2;
