class FastScanner {
    private final InputStream in = System.in;
    private final byte[] buffer = new byte[1024];
    private int ptr = 0;
    private int buflen = 0;
    private boolean hasNextByte() {
        if (ptr < buflen) {
            return true;
        }else{
            ptr = 0;
            try {
                buflen = in.read(buffer);
            } catch (IOException e) {
                e.printStackTrace();
            }
            if (buflen <= 0) {
                return false;
            }
        }
        return true;
    }
    private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
    private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
    public boolean hasNext() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++; return hasNextByte();}
    public String next() {
        if (!hasNext()) throw new NoSuchElementException();
        StringBuilder sb = new StringBuilder();
        int b = readByte();
        while(isPrintableChar(b)) {
            sb.appendCodePoint(b);
            b = readByte();
        }
        return sb.toString();
    }
    public long nextLong() {
        if (!hasNext()) throw new NoSuchElementException();
        long n = 0;
        boolean minus = false;
        int b = readByte();
        if (b == '-') {
            minus = true;
            b = readByte();
        }
        if (b < '0' || '9' < b) {
            throw new NumberFormatException();
        }
        while(true){
            if ('0' <= b && b <= '9') {
                n *= 10;
                n += b - '0';
            }else if(b == -1 || !isPrintableChar(b)){
                return minus ? -n : n;
            }else{
                throw new NumberFormatException();
            }
            b = readByte();
        }
    }
    public int nextInt() {
        long nl = nextLong();
        if (nl < Integer.MIN_VALUE || nl > Integer.MAX_VALUE) throw new NumberFormatException();
        return (int) nl;
    }
    public double nextDouble() { return Double.parseDouble(next());}
}

public class Main {
	public static class UnionFind {
		int[] param;
		int[] rank;
		int N;
		int xlen;
		public UnionFind(int n) {
			N=n;
			param = new int[N];
			rank = new int[N];
			for(int i=0; i<N; i++)
				param[i] = i;
		}
		public void init() {
			for(int i=0; i<N; i++)
				param[i] = i;
			Arrays.fill(rank, 0);
		}
		public UnionFind(int x, int y) {
			this(x*y);
			xlen=y;
		}
		public boolean same(int a, int b) {
			return getRoot(a)==getRoot(b);
		}
		public boolean same(int x1, int y1, int x2, int y2) {
			return same(x1*xlen+y1, x2*xlen+y2);
		}
		public int getRoot(int a) {
			if(param[a]==a) return a;
			else return (param[a] = getRoot(param[a]));
		}
		public void unite(int a, int b) {
			a = getRoot(a);
			b = getRoot(b);
			if(a==b)
				return;
			if(rank[a] < rank[b]) {
				param[a] = b;
			} else {
				param[b] = a;
				if(rank[a]==rank[b])
					rank[a]++;
			}
		}
		public void unite(int x1, int y1, int x2, int y2) {
			unite(x1*xlen+y1, x2*xlen+y2);
		}
	}
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int N = sc.nextInt();
		int M = sc.nextInt();
		int K = sc.nextInt();
		HashMap<Integer, HashSet<Integer>> exclude = new HashMap<>();
		UnionFind uf = new UnionFind(N);
		for(int i=0; i<M; i++) {
			int A = sc.nextInt()-1;
			int B = sc.nextInt()-1;
			uf.unite(A, B);
			HashSet<Integer> setA = exclude.get(A);
			if(setA==null) {
				setA = new HashSet<Integer>();
			}
			setA.add(B);
			exclude.put(A, setA);
			HashSet<Integer> setB = exclude.get(B);
			if(setB==null) {
				setB = new HashSet<Integer>();
			}
			setB.add(A);
			exclude.put(B, setB);
		}
		for(int i=0; i<K; i++) {
			int C = sc.nextInt()-1;
			int D = sc.nextInt()-1;
			if(!uf.same(C, D))
				continue;
			HashSet<Integer> setC = exclude.get(C);
			if(setC==null) {
				setC = new HashSet<Integer>();
			}
			setC.add(D);
			exclude.put(C, setC);
			HashSet<Integer> setD = exclude.get(D);
			if(setD==null) {
				setD = new HashSet<Integer>();
			}
			setD.add(C);
			exclude.put(D, setD);
		}
		int[] count = new int[N];
		for(int i=0; i<N; i++) {
			int root = uf.getRoot(i);
			count[root]++;
		}
		for(int i=0; i<N; i++) {
			int candidate = count[uf.getRoot(i)] - 1 - exclude.get(i).size();
			System.out.print(candidate + (i==N-1 ? "\n" : " "));
		}
		sc.close();
	}
}

public class Main {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		while (true) {
			int n = sc.nextInt();
			if (n == 0) {
				sc.close();
				break;
			}
			String firstGroup = "";
			Map<String, ArrayList<String>> groups = new HashMap<String, ArrayList<String>>();
			for (int z = 0; z < n; z++) {
				String line = sc.next();
				String group = line.split(":")[0];
				if (z == 0) {
					firstGroup = group;
				}
				groups.put(group, new ArrayList<String>());
				String[] members = line.split(":")[1].split(",");
				for (int i = 0; i < members.length; i++) {
					groups.get(group).add(
							members[i].trim().replaceAll("\\.", ""));
				}
			}
			ArrayList<String> ansList = new ArrayList<String>();
			Deque<String> deque = new ArrayDeque<String>();
			deque.add(firstGroup);
			while (!deque.isEmpty()) {
				String person = deque.poll();
				if (groups.get(person) == null && !ansList.contains(person)) {
					ansList.add(person);
				} else if (groups.get(person) != null) {
					for (String child : groups.get(person)) {
						deque.add(child);
					}
				}
			}
			System.out.println(ansList.size());
		}
	}
}

