public class Main {
	public static class Vertex {
		int     digree;
		Vertex  neighbor[];
		int     cost[];
		int     minCost;
		boolean flag;
		int label;
		Vertex(int n, int k) {
			digree = 0;
			neighbor = new Vertex[n];
			cost = new int[n];
			minCost = Integer.MAX_VALUE;
			flag = false;
			label = k;
		}
	}
	public static void main(String args[]) {
		Scanner sc = new Scanner(System.in);
		int n, k;
		int order;
		Vertex v[];
		n = sc.nextInt();
		k = sc.nextInt();
		v = new Vertex[n];
		for (int i = 0; i < n; i++) {
			v[i] = new Vertex(n, i + 1);
		}
		for (int i = 0; i < k; i++) {
			order = sc.nextInt();
			if (order == 1) {
				int v1 = sc.nextInt() - 1;
				int v2 = sc.nextInt() - 1;
				int cost = sc.nextInt();
				boolean flag = true;
				int index = 0;
				for (int j = 0; j < v[v1].digree && flag; j++) {
					if (v[v1].neighbor[j] == v[v2]) {
						v[v1].cost[j] = (cost < v[v1].cost[j]) ? cost : v[v1].cost[j];
						flag = false;
					}
				}
				for (int j = 0; j < v[v2].digree && !flag; j++) {
					if (v[v2].neighbor[j] == v[v1]) {
						v[v2].cost[j] = (cost < v[v2].cost[j]) ? cost : v[v2].cost[j];
					}
				}
				if (flag) {
					v[v1].neighbor[v[v1].digree] = v[v2];
					v[v1].cost[v[v1].digree++] = cost;
					v[v2].neighbor[v[v2].digree] = v[v1];
					v[v2].cost[v[v2].digree++] = cost;
				}
			} else if (order == 0) {
				int v1 = sc.nextInt() - 1;
				int v2 = sc.nextInt() - 1;
				System.out.println(dijkstra(v, n, v1, v2));
			}
		}
	}
	public static int dijkstra(Vertex v[], int n, int start, int goal) {
		boolean flag = true;
		for (int i = 0; i < n; i++) {
			v[i].flag = false;
			v[i].minCost = Integer.MAX_VALUE;
		}
		v[start].minCost = 0;
		v[start].flag = true;
		while (!v[goal].flag) {
			flag = false;
			int fromIndex = -1;
			int minIndex  = -1;
			for (int i = 0; i < n; i++) {
				if (v[i].flag) {
					for (int j = 0; j < v[i].digree; j++) {
						if (!v[i].neighbor[j].flag) {
							if (v[i].minCost + v[i].cost[j] < v[i].neighbor[j].minCost) {
								v[i].neighbor[j].minCost = v[i].minCost + v[i].cost[j];
							}
							if (minIndex == -1 || v[i].neighbor[j].minCost < v[i].neighbor[minIndex].minCost) {
								fromIndex = i;
								minIndex  = j;
							}
						}
					}
				}
			}
			if (minIndex != -1) {
				v[fromIndex].neighbor[minIndex].flag = true;
				flag = true;
			}
			if (!flag) {
				return -1;
			}
		}
		return v[goal].minCost;
	}
}

public class Main {
  public static void main (String args[]) {
    Scanner sc = new Scanner(System.in);
    int N = sc.nextInt();
    int three = N/3;
    ArrayList<Integer>[] edge  = new ArrayList[N];
    for(int i = 0; i < N; i++){
      edge[i] = new ArrayList<>();
    }
    for(int i = 0; i < N-1; i++) {
      int from = sc.nextInt()-1;
      int to = sc.nextInt()-1;
      edge[from].add(to);
      edge[to].add(from);
    }
    int ans[] = new int[N];
    int depth[] = new int[N];
    depth[0] = 1;
    int ki, gu;
    ki = gu = 0;
    ArrayDeque<Integer> adq = new ArrayDeque<>();
    adq.add(0);
    while(adq.size()>0){
      int now = adq.pollFirst();
      if(depth[now]==1){
        gu++;
      } else {
        ki++;
      }
      for(int next : edge[now]){
        if(depth[next]==0){
          depth[next] = -depth[now];
          adq.addLast(next);
        }
      }
    }
    boolean check[] = new boolean[N];
    int k, l, m;
    k = l = 0;
    m = 1;
    if(ki<=three){
      for(int i=0;i<N;i++){
        if(depth[i]==-1) {
            ans[i]=3*m;
            check[ans[i]-1]=true;
            m++;
        }
      }
    }
    else if(gu<=three){
      for(int i=0;i<N;i++){
        if(depth[i]==1) {
            ans[i]=3*m;
            check[ans[i]-1]=true;
            m++;
        }
      }
    }
    else {
      for(int i=0;i<N;i++){
        if(depth[i]==-1&&k<=three) {
          ans[i]=3*k+1;
          check[ans[i]-1]=true;
          k++;
        }
        else if(depth[i]==1&&l<=three){
          ans[i]=3*l+2;
          check[ans[i]-1]=true;
          l++;
        }
      }
    }
    int c = 0;
    for(int i=0;i<N;i++){
      if(ans[i]!=0){
        System.out.print(ans[i]+" ");
      } else {
        while(check[c]){ 
          c++;
        }
        System.out.print((c+1)+" ");
        c++;
      }
    }
    for(int i = 0; i < N; i++){
      System.err.println(depth[i]);
    }
  }
}

public class Main {
	final static int INF = Integer.MAX_VALUE;
	static boolean[][] conn;
	static int[] memo;
	static int n;
	public static void main(String[] args) {
		Locale.setDefault(Locale.ENGLISH);
		Scanner in = new Scanner(new BufferedReader(new InputStreamReader(System.in)));
		solve(in, System.out);
		in.close();
	}
	protected static void solve(Scanner in, PrintStream out) {
		n = in.nextInt();
		int m = in.nextInt();
		conn = new boolean[n + 1][n + 1];
		for (int j = 0; j < m; j++)
			conn[in.nextInt()][in.nextInt()] = true;
		int ret = solveLine();
		out.printf(Locale.ENGLISH, "%s%n", ret);
	}
	private static int solveLine() {
		int ans = 0;
		for (int i = 1; i <= n; i++) {
			memo = new int[n + 1];
			Arrays.fill(memo, -1);
			ans = max(ans, lp(i));
		}
		return ans;
	}
	private static int lp(int pos) {
		if (memo[pos] >= 0) return memo[pos];
		int ans = 0;
		for (int i = 1; i <= n; i++) {
			if (conn[pos][i]) {
				ans = max(ans, 1 + lp(i));
			}
		}
		return memo[pos] = ans;
	}
}

public class Main{
	public static class Node{
		ArrayList<Integer> to;
		int p_or_l;
		Node(){
			to = new ArrayList<Integer>();
		}
	}
	static Scanner sn = new Scanner(System.in);
	static int N = sn.nextInt();
	static int M = sn.nextInt();
	static Node[] peple = new Node[1002];
	static Node[] lang = new Node[1002];
	static boolean[] visit = new boolean[1002];
	public static void main(String[] args){		
		for(int i = 0; i < N; ++i){
			peple[i] = new Node();
			peple[i].p_or_l = 0;
			visit[i] = false;
		}
		for(int i = 0;i < M; ++i){
			lang[i] = new Node();
			lang[i].p_or_l = 1;		
		}
		for(int i = 0; i < N; ++i){
			int K = sn.nextInt();
			for(int j = 0; j < K; ++j){
				int l = sn.nextInt();
				peple[i].to.add(l-1);
				lang[l-1].to.add(i);
			}
		}
		visit[0] = true;
		solve(peple[0]);
		check();
	}
	public static void solve(Node p){
		if(p.to.isEmpty()) return;
		for(int i = 0; i < p.to.size(); ++i){		
			int next = p.to.get(i);			
			if(p.p_or_l == 0){				
				solve(lang[next]);
			}
			else{
				if(visit[next]) continue;
				visit[next] = true;
				solve(peple[next]);
			}
		}
	}
	public static void check(){
		boolean flag = true;
		for(int i = 0; i < N; ++i){
			if(!visit[i]){
				flag = false;
				break;
			}  
		}
		if(flag) System.out.println("YES");
		else System.out.println("NO");
	}
}

class Main {
    static int[] primes;
    static List<Integer> primeList;
    static {
        int length = (int) Math.sqrt(500000000) + 1;
        primes = new int[length];
        int max = (int) Math.sqrt(length) + 1;
        for (int i = 2; i < max; i++) {
            if (primes[i] == 1) {
                continue;
            }
            int cur = i * i;
            while (cur < length) {
                primes[cur] = 1;
                cur += i;
            }
        }
        primeList = new ArrayList<>();
        for (int i = 2; i < length; i++) {
            if (primes[i] == 0) {
                primeList.add(i);
            }
        }
    }
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        int K = in.nextInt();
        int[] A = new int[N];
        for (int i = 0; i < N; i++) {
            A[i] = in.nextInt();
        }
        int sum = 0;
        for (int a : A) {
            sum += a;
        }
        SortedSet<Integer> factors = new TreeSet<>(Comparator.reverseOrder());
        List<Integer> tmpPrimes = new ArrayList<>(primeList);
        while (sum > 1 && tmpPrimes.size() > 0) {
            if (sum % tmpPrimes.get(0) == 0) {
                sum /= tmpPrimes.get(0);
                factors.addAll(factors.stream().map(i -> i*tmpPrimes.get(0)).collect(Collectors.toList()));
                factors.add(tmpPrimes.get(0));
            } else {
                tmpPrimes.remove(0);
            }
        }
        factors.add(sum);
        boolean hasResult = false;
        for (int i : factors) {
            if (helper(i, K, A)) {
                System.out.println(i);
                hasResult = true;
                break;
            }
        }
        if (!hasResult) {
            System.out.println(1);
        }
    }
    private static boolean helper(int i, int k, int[] A) {
        int max = 0, sum = 0;
        for (int a : A) {
            int tmp = a % i;
            max = Math.max(max, tmp);
            sum += tmp;
        }
        return sum - max <= k;
    }
}

