public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        FasterScanner in = new FasterScanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskC solver = new TaskC();
        solver.solve(1, in, out);
        out.close();
    }
    static class TaskC {
        public void solve(int testNumber, FasterScanner in, PrintWriter out) {
            BigDecimal n = new BigDecimal(in.nextInt());
            BigDecimal m = new BigDecimal(in.nextInt() - 1);
            double d = in.nextDouble();
            out.println(m.divide(n));
        }
    }
    static class FasterScanner {
        private InputStream in;
        private byte[] buffer;
        private int bufPointer;
        private int bufLength;
        public FasterScanner(InputStream in) {
            this.in = in;
            buffer = new byte[1024];
        }
        private boolean hasNextByte() {
            if (bufPointer < bufLength) return true;
            bufPointer = 0;
            try {
                bufLength = in.read(buffer);
            } catch (IOException e) {
                e.printStackTrace();
            }
            return (bufLength > 0);
        }
        private int readByte() {
            if (hasNextByte()) return buffer[bufPointer++];
            return -1;
        }
        private static boolean isPrintableChar(int c) {
            return (c >= 33 && c <= 126);
        }
        public boolean hasNext() {
            while (hasNextByte() && !isPrintableChar(buffer[bufPointer])) bufPointer++;
            return hasNextByte();
        }
        public String next() {
            if (!hasNext()) throw new NoSuchElementException();
            StringBuilder sb = new StringBuilder();
            int b = readByte();
            while (isPrintableChar(b)) {
                sb.appendCodePoint(b);
                b = readByte();
            }
            return sb.toString();
        }
        public int nextInt() {
            long nl = nextLong();
            if (nl < Integer.MIN_VALUE || nl > Integer.MAX_VALUE) throw new NumberFormatException();
            return (int) nl;
        }
        public long nextLong() {
            if (!hasNext()) throw new NoSuchElementException();
            long n = 0;
            boolean minus = false;
            int b = readByte();
            if (b == '-') {
                minus = true;
                b = readByte();
            }
            if (b < '0' || b > '9') throw new NumberFormatException();
            while (true) {
                if (b >= '0' && b <= '9') {
                    n *= 10;
                    n += b - '0';
                } else if (b == -1 || !isPrintableChar(b)) {
                    return minus ? -n : n;
                } else {
                    throw new NumberFormatException();
                }
                b = readByte();
            }
        }
        public double nextDouble() {
            return Double.parseDouble(next());
        }
    }
}

public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        int H = in.nextInt();
        int W = in.nextInt();
        int[][] A = new int[H][W];
        for (int i=0;i<H;i++) {
            for (int j=0;j<W;j++) {
                A[i][j] = Integer.parseInt(in.next());
            }
        }
        int[][] B = new int[H][W];
        for (int i=0;i<H;i++) {
            for (int j=0;j<W;j++) {
                B[i][j] = Integer.parseInt(in.next());
            }
        }
        int[][] C = new int[H+1][W+1];
        for (int i=0;i<H;i++) {
            for (int j=0;j<W;j++) {
                C[i+1][j+1] = Math.abs(A[i][j]-B[i][j]);
            }
        }
        int AA = 20000;
        int[][][] dp = new int[H+1][W+1][10*AA];
        dp[0][1][AA]=1;
        dp[1][0][AA]=1;
        for (int i=0;i<=H;i++) {
            for (int j=0;j<=W;j++) {
                for (int k=0;k<3*AA;k++) {
                    if (i<H && j>0) {
                        if (dp[i][j][k]==1) dp[i+1][j][k+C[i+1][j]]=1;
                        if (k-C[i+1][j]>=0) {
                            if (dp[i][j][k]==1) dp[i+1][j][k-C[i+1][j]]=1;
                        }
                    }
                    if (j<W && i>0) {
                        if (dp[i][j][k]==1) dp[i][j+1][k+C[i][j+1]]=1;
                        if (k-C[i][j+1]>=0) {
                            if (dp[i][j][k]==1) dp[i][j+1][k-C[i][j+1]]=1;
                        }
                    }
                }
            }
        }
        long ans = 1_000_000_000L;
        for (int k=3*AA;k>=0;k--) {
            if (dp[H][W][k]==1) ans = Math.min(ans, Math.abs(k-AA));
        }
        out.println(ans);
        out.close();
    }
    static class InputReader {
        public BufferedReader reader;
        public StringTokenizer tokenizer;
        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }
        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }
        public int nextInt() {
            return Integer.parseInt(next());
        }
        public long nextLong() {
            return Long.parseLong(next());
        }
    }
}

