class Main{
	public static void main(String[] args) {
		SC sc=new SC(System.in);
		ArrayList<Integer> unko=new ArrayList<Integer>();
		int N=sc.nextInt();
		for(int i=0; i<N; i++) {
			unko.add(sc.nextInt());
		}
		Collections.sort(unko);
		int kosu=0;
		while(true){
			int a=unko.get(unko.size()-1);
			int max=unko(a);
			int hosu=max-a;
			int ban=Collections.binarySearch(unko, hosu);
			if(ban>=0) {
				kosu++;
				unko.remove(unko.size()-1);
				unko.remove(ban);
			}
			else {
				unko.remove(unko.size()-1);
			}
			if(unko.size()==0) {
				break;
			}
		}
		System.out.println(kosu);
	}
	static class SC {
		private BufferedReader reader = null;
		private StringTokenizer tokenizer = null;
		public SC(InputStream in) {
			reader = new BufferedReader(new InputStreamReader(in));
		}
		public String next() {
			if (tokenizer == null || !tokenizer.hasMoreTokens()) {
				try {
					tokenizer = new StringTokenizer(reader.readLine());
				} catch (IOException e) {
					throw new UncheckedIOException(e);
				}
			}
			return tokenizer.nextToken();
		}
		public int nextInt() {
			return Integer.parseInt(next());
		}
		public long nextLong() {
			return Long.parseLong(next());
		}
	}
	static int unko(int a) {
		int k=0;
		int b=a;
		while(b>0) {
			k++;
			b/=2;
		}
		int aaa=1;
		for(int i=0; i<k; i++) {
			aaa*=2;
		}
		return aaa;
	}
}

class Main {
	public static void main(String[] args) {
		Scanner sc=new Scanner(System.in);
		int n=sc.nextInt();
		int m=sc.nextInt();
		int k=sc.nextInt();
		int count=0;
		long sum=0;
		int a[]=new int[n];
		int b[]=new int[m];
		for(int i=0;i<n;i++)
		{
			a[i]=sc.nextInt();
		}
		for(int j=0;j<m;j++)
		{
			b[j]=sc.nextInt();
		}
		int dp[][]=new int[n+1][m+1];
		for(int j[]:dp)
		{
			Arrays.fill(j,-1);
		}
		System.out.println(solve(a,b,0,0,k,0,dp));
	}
	public static int solve(int a[],int b[],int i,int j,int k,long sum,int dp[][])
	{
		if(dp[i][j]!=-1)
		{
			return -1;
		}
		if(sum>k)
		{
			return -1;
		}
		if(sum==k)
		{
			return 0;
		}
		if(i>=a.length && j>=b.length)
		{
			return 0;
		}
		int option1=0;
		int option2=0;
		if(i<a.length)
		{
			option1=1+solve(a,b,i+1,j,k,sum+a[i],dp);
		}
		if(j<b.length)
		{
			option2=1+solve(a,b,i,j+1,k,sum+b[j],dp);
		}
		return dp[i][j]=Math.max(option1,option2);
	}
}

public class Main {
	public static void main(String[] args) throws Exception{
		BufferedReader r = new BufferedReader(new InputStreamReader(System.in));
		String str[] = r.readLine().split(" ");
		int max = Integer.parseInt(str[0]);
		Queue q = new Queue(max);
		int qtime = Integer.parseInt(str[1]);
		for(int i = 0 ; i < max ; i++ ){
			str = r.readLine().split(" ");
			q.enqueue(new Process(str[0], Integer.parseInt(str[1])));
		}
		int elapsedTime = 0;
		int count = 0;
		while(true){
			if(max <= count) break;
			Process p = q.dequeue();
			int time = p.getTime();
			if(time <= qtime){
				elapsedTime+= time;
				count++;
				System.out.println(p.getName() +" "+elapsedTime);
			}else{
				p.setTime(time - qtime);
				elapsedTime += qtime;
				q.enqueue(p);
			}
		}
	}
}
class Queue{
	Process[] processAry;
	int top;
	int tail;
	int max;
	public Queue(int max){
		top = tail = 0;
		processAry = new Process[max];
		this.max = max;
	}
	public void enqueue(Process process) throws Exception{
		if(isFull()){
			throw new Exception("FULL");
		}else{
			processAry[top] = process;
			top = (top + 1) % max;
		}
	}
	public Process dequeue() throws Exception{
			Process p = processAry[tail];
			tail = (tail + 1) % max;
			return p;
	}
	public boolean isFull(){
		return tail == top + 1 % max;
	}
	public boolean isEmpty(){
		return top == tail;
	}
}
