public class Main {
	static PrintWriter out;
	static StringBuilder sb;
	static int mod = 998244353;
	static int inf = (int) 1e15;
	static ArrayList<Integer>[] ad;
	static int n, s;
	static int[][][] memo;
	static int[] a;
	public static void main(String[] args) throws Exception {
		Scanner sc = new Scanner(System.in);
		out = new PrintWriter(System.out);
		n = sc.nextInt();
		s = sc.nextInt();
		a = new int[n];
		for (int i = 0; i < n; i++)
			a[i] = sc.nextInt();
		memo = new int[n][s + 1][3];
		for (int[][] u : memo)
			for (int[] j : u)
				Arrays.fill(j, -1);
		System.out.println(dp(0, s, 0));
		out.close();
	}
	static int dp(int i, int sum, int st) {
		if (i == n) {
			if (sum != 0)
				return 0;
			if (st != 0)
				return 1;
		}
		if (sum < 0)
			return 0;
		if (memo[i][sum][st] != -1)
			return memo[i][sum][st];
		int ans = 0;
		if (st == 0) {
			ans = dp(i + 1, sum, 0) + dp(i + 1, sum, 2);
			if(ans>=mod)
				ans-=mod;
			ans = (ans + dp(i + 1, sum, 1) + dp(i + 1, (sum - a[i]), 1)) ;
			if(ans>=mod)
				ans-=mod;
		} else if (st == 1) {
			ans = (ans + dp(i + 1, sum, 1) + dp(i + 1, (sum - a[i]), 1)) ;
			if(ans>=mod)
				ans-=mod;
			ans = (ans + dp(i + 1, sum, 2)) ;
			if(ans>=mod)
				ans-=mod;
		} else {
			ans = (ans + dp(i + 1, sum, st)) ;
			if(ans>=mod)
				ans-=mod;
		}
		if(ans>=mod)
			ans-=mod;
		return memo[i][sum][st] = ans;
	}
