            		if(dp[i-1][v]!=-1)
            			dp[i][v] = Math.min(dp[i-1][v], dp[i-1][v-(int)arr[i][1]]+arr[i][0]);
            		else
            			dp[i][v] = dp[i-1][v-(int)arr[i][1]]+arr[i][0];
            	}
            	else{
            		dp[i][v] = dp[i-1][v];
            	}
        	}
        }
