        int n = nmx[0];
        int m = nmx[1];
        int x = nmx[2];
        int A[][] = new int[n][m+1];
        for(int i=0;i<n;i++)
        {
            A[i] = Arrays.stream(br.readLine().split(" ")).mapToInt(Integer::parseInt).toArray();
        }
        int ans = Integer.MAX_VALUE;
        for(int i=0;i<(1<<n);i++)
        {
            ArrayList<Integer> al = new ArrayList<Integer>();
            for(int j=0;j<n;j++)
            {
                if(((i>>j)&1)==1)
                al.add(j);
            }
            int a=0,b=0,c=0;
            int cost = 0;
            for(int temp:al)
            {
                a += A[temp][1];
                b +=A[temp][2];
                c +=A[temp][3];
                cost+=A[temp][0];
            }
            if(a>=x && b>=x && c>=x)
            ans = Math.min(ans,cost);
        }
        if(ans == Integer.MAX_VALUE)
        ans = -1;
        bw.write(Integer.toString(ans));
    }
